#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\obsidian\mystery\node_modules\.pnpm\astro@5.16.6_@types+node@25_562b2634534966f9b008b04d6567530e\node_modules\astro\node_modules;D:\obsidian\mystery\node_modules\.pnpm\astro@5.16.6_@types+node@25_562b2634534966f9b008b04d6567530e\node_modules;D:\obsidian\mystery\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/obsidian/mystery/node_modules/.pnpm/astro@5.16.6_@types+node@25_562b2634534966f9b008b04d6567530e/node_modules/astro/node_modules:/mnt/d/obsidian/mystery/node_modules/.pnpm/astro@5.16.6_@types+node@25_562b2634534966f9b008b04d6567530e/node_modules:/mnt/d/obsidian/mystery/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../astro.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../astro.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../astro.js" $args
  } else {
    & "node$exe"  "$basedir/../../astro.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
